const Joke = ({id, text}) => {
  const [votes, setVotes] = useState(0);

    return (
      <div className="Joke">
        <div className="Joke-votearea">
          <button onClick={() => setVotes(vote => vote + 1)}>
            <i className="fas fa-thumbs-up" />
          </button>

          <button onClick={() => setVotes(vote => vote - 1)}>
            <i className="fas fa-thumbs-down" />
          </button>

          {votes}
        </div>

        <div className="Joke-text">{text}</div>
      </div>
    );

}

export default Joke;
