import React, { useState, useEffect } from "react";
import axios from "axios";
import Joke from "./Joke";
import "./JokeList.css";

const JokeList = ({numJokesToGet = 5}) => {
  const [jokes, setJokes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [changeJokeOrder, setChangeJokeOrder] = useState(false);

  const generateNewJokes = () => {
    setJokes([]);
    setIsLoading(true);
    localStorage.clear();
    setChangeJokeOrder(false);
  }

  const displayJokesOnUI = () => {
      return (
          jokes.map((j) => {
            const data = {id: j.id, votes: 0, text: j.joke, key: j.id};
            localStorage.setItem(`${j.id}`, JSON.stringify(data));
          return (
              <Joke
            text={j.joke}
            key={j.id}
            id={j.id}
            updateLocalStorageOnVote={updateLocalStorageOnVote}
          />
          )})
        )
  }

// useEffect(() => {
//   displayJokesOnUI();
// }, [changeJokeOrder])

  const displayNewOrderdJokesOnUI = () => {
    const storage = Object.values(localStorage); 
    const loopedStorage = storage.map((j) => {
      return JSON.parse(j);
    })
    // console.log(loopedStorage, 'loopedStorage')
    // console.log(storage, 'storage in new order')
    // console.log(jokes, 'jokes in NO')
      const sortedStorage = loopedStorage.sort((a, b) => b.votes - a.votes);

      console.log(sortedStorage, 'sortedStorage')

    return (
      jokes.map((j) => {
      return (
          <Joke
        text={j.joke}
        key={j.id}
        id={j.id}
        updateLocalStorageOnVote={updateLocalStorageOnVote}
      />
      )})
    )
  }

  useEffect(() => {
    const getJokes = async() => {
      try {
        let seenJokes = new Set();
        while (jokes.length < numJokesToGet) {
          let res = await axios.get("https://icanhazdadjoke.com", {
            headers: { Accept: "application/json" }
          });
          let { ...joke } = res.data;

          if (!seenJokes.has(joke.id)) {
            seenJokes.add(joke.id);
            jokes.push({ ...joke });
          } else {
            console.log("duplicate found!");
          }
        }
        setIsLoading(false);
      } catch (err) {
        console.error(err);
      }
    }

    getJokes();
  }, [generateNewJokes])

  const updateLocalStorageOnVote = (id, votes, text, key) => {
      const data = {id: id, votes: votes, text: text, key: key};
      localStorage.setItem(`${id}`, JSON.stringify(data));
      setChangeJokeOrder(true);
  }

  // const updateVotingLeaderboard = (id, votes, jokeNum) => {
  //   updateLocalStorageOnVote(id, votes, jokeNum);
  //   const filteredStorage = [];
  //   let values = Object.values(localStorage);
  //   for(let v of values){
  //     if(v !== 'INFO'){
  //       filteredStorage.push(JSON.parse(v));
  //     }
  //   }
  //   const sortedStorage = filteredStorage.sort((a, b) => b.votes - a.votes);
  //   console.log(sortedStorage, 'sortedStorage')



    // jokes.filter((j) => {
    //   if(s.id === j.id){
    //     return [jokes[i], ...jokes]
    //   }
    // })

    // const orderJokes = sortedStorage.forEach((s) => {
    //     for(let i = 0; i < jokes.length; i++){
    //       if(s.id === jokes[i].id){
    //         setJokes(jokes.filter((j) => j !== jokes[i]));
    //         console.log(jokes, 'THIS JOKES')

    //         return [jokes[i], ...jokes]
    //       }
    //     }
    //   })
    //   setJokes(orderJokes);
    //   console.log(orderJokes, 'orderJokes')
    // };

  // useEffect(() => {
  //   const changeJokeOrder = () => {
  //     console.log(jokes, 'jokes in useeffect')
  //   }

  //   changeJokeOrder();
  // }, [updateVotingLeaderboard])

  const showLoadingSpinner = () => {
    if(isLoading === true){
      return (
        <div className="loading">
         <i className="fas fa-4x fa-spinner fa-spin" />
       </div>
      )
    }
  }

    return (
      <div className="JokeList">
        <button
          className="JokeList-getmore"
          onClick={() => generateNewJokes()}
        >
          Get New Jokes
        </button>
        <div>
          {showLoadingSpinner()}
        </div>

        {changeJokeOrder === false ? displayJokesOnUI() : displayNewOrderdJokesOnUI()}

      </div>
    )
}

export default JokeList;
